mapper.update:
UPDATE tb_activity
activity_name = #{activityName}, activity_desc = #{activityDesc}, activity_date = #{activityDate}, location = #{location}, max_participants = #{maxParticipants}, current_participants = #{currentParticipants}, organizer_id = #{organizerId}, cover_image = #{coverImage}, status = #{status},
WHERE id = #{id}

UPDATE tb_activity
SET current_participants = current_participants + #{increment}
WHERE id = #{id}
